describe('update-global-agent-options', () => {
  test('non copy option', () => {
    let from = {
      options: {
        random: 'value'
      }
    };
    let to = {
      options: {}
    };
    _updateGlobalAgentOptions.AGENT_OPTIONS_TO_COPY.forEach(key => {
      from.options[key] = key;
    });
    (0, _updateGlobalAgentOptions.updateGlobalAgentOptions)(from, to);
    expect(Object.keys(to.options)).toHaveLength(_updateGlobalAgentOptions.AGENT_OPTIONS_TO_COPY.length);
    expect(to.options).not.toHaveProperty('random');
  });
});